var logger = require('../');
console.log('');
describe('chalk-log',function(){
	
	describe('#log',function(){
		it('Should print normal log',function(){
			logger.log('This is log');
		});
	});
	
	describe('#note',function(){
		it('Should print white bg',function(){
			logger.note('This is note');
		});
	});
	
	describe('#error',function(){
		it('Should print red bg',function(){
			logger.error('This is error');
		});
	});
	
	describe('#progress',function(){
		it('Should print blue bg',function(){
			logger.progress('This is progress');
		});
	});
	
	describe('#warn',function(){
		it('Should print yellow bg',function(){
			logger.warn('This is warning');
		});
	});
	
	describe('#ok',function(){
		it('Should print yellow bg',function(){
			logger.ok('This is OK');
		});
	});
	
});
